import { useState, useEffect } from 'react';
import api from '../../services/api';
import './banner.css'

function Banner() {
  const [movies, setMovies] = useState([]);

  useEffect(()=> {
    async function loadMovies(){
      const response = await api.get(`movie/popular`, {
        params: {
          api_key: "1f6b33fb81a101dccbcc1b538423c585",
          page: 1,
        }
      })
      .then((response) => {
        setMovies(response.data.results);
      })
    }

    loadMovies();

  }, [])

  const images = [];

  movies.map((movie) => {
    images.push(movie.backdrop_path)
  })



  if(images.length !== 0){
    let randomChange = document.getElementById('randomChange')
    const imgCount = images.length;
    let number = Math.floor(Math.random() * imgCount)

    function imgDisp(num) {
      randomChange.style.backgroundImage = `url(https://image.tmdb.org/t/p/original/zGLHX92Gk96O1DJvLil7ObJTbaL.jpg)`
    }
    console.log(randomChange)
    console.log(images[number])

    imgDisp()
  }

  // `http://image.tmdb.org/t/p/original/${images[number]}`
  return(
    <div className='banner-container'  id='randomChange'>

    </div>
  )
}

export default Banner;
