import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../../services/api";
import './home.css';

// API URL: movie/now_playing?api_key=1f6b33fb81a101dccbcc1b538423c585

function Home() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1)
  const [loading, setLoading] = useState(true);

  useEffect(()=> {
    async function loadMovies(){
      const response = await api.get("movie/popular", {
        params: {
          api_key: "1f6b33fb81a101dccbcc1b538423c585",
          page: page,
        }
      })


      // console.log(response.data.results.slice(0, 10))
      //using SLICE because the array returned from the API has a lot of movies. So I want to show only the first 10.
      setMovies(prev => response.data.results);
      console.log("response.data.results= ", response.data.results[0].title);
      if(movies[0] !== undefined){console.log("movies= ", movies[0].title)};

      if (page !== response.data.total_pages && page < 5) {
        let sumPage = page + 1;
        setPage(sumPage);
      }
      setLoading(false);
    }

    loadMovies();

  }, [page])

  if(loading) {
    return(
    <div className="loading">
      <h1>Loading...</h1>
    </div>
    )
  }

  return(
    <div className="container">
      <div className="movies-list">
        {movies.map((movie) => {
          return(
            <div key={movie.id}>
              <Link to={`/movie/${movie.id}`}>
                <img src={`http://image.tmdb.org//t/p/original/${movie.poster_path}`} alt={movie.title}/>                
              </Link>
              <strong>{movie.title}</strong>
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default Home;
